parameters.jsCode:
// Подготовка данных для отправки через MCP Telegram
const input = $input.first().json;
const outMessage = input.out_message || {};

// Берём данные из корня или из out_message
let chatId = input.external_chat_id || outMessage.external_chat_id || input.chat_id || outMessage.chat_id || '';
if (chatId && chatId.startsWith('tg_')) {
  chatId = chatId.substring(3);
}

// text может быть в разных местах
const text = input.message_text || outMessage.message_text || input.text || outMessage.text || input.response_text || '';

// tenant_id для запроса bot_token
const tenantId = input.tenant_id || outMessage.tenant_id || '';

return {
  chat_id: chatId,
  text: text,
  parse_mode: 'Markdown',
  tenant_id: tenantId,
  appeal_id: input.appeal?.id || outMessage.appeal_id || input.appeal_id,
  client_id: input.client?.id || outMessage.client_id || input.client_id,
  action_source: input.action_source || outMessage.action_source || 'response'
};

const prepared = $('Prepare Message').first().json;
const tokenResult = $input.first().json;

// Получаем bot_token из результата запроса
const botToken = tokenResult?.bot_token || '';

if (!botToken) {
  console.error('Bot token not found for tenant:', prepared.tenant_id);
}

return {
  ...prepared,
  bot_token: botToken
};

const response = $input.first().json;
const preparedData = $('Merge Bot Token').first().json;

if (!response.success) {
  console.error('Telegram send failed:', response);
}

// Определяем тип touchpoint
const actionSource = preparedData.action_source || '';
const touchpointDirection = (actionSource === 'promo' || actionSource === 'greeting' || actionSource === 'broadcast') ? 'promo' : 'outbound';

return {
  ...preparedData,
  telegram_response: response,
  message_id: response.message_id?.toString() || Date.now().toString(),
  sent_at: new Date().toISOString(),
  touchpoint_direction: touchpointDirection
};

