// Parse task from Redis
const raw = $input.item.json.propertyName;

let task;
if (typeof raw === 'string') {
  try {
    const parsed = JSON.parse(raw);
    task = Array.isArray(parsed) ? parsed[0] : parsed;
  } catch (e) {
    throw new Error('Failed to parse task: ' + e.message);
  }
} else if (Array.isArray(raw)) {
  task = raw[0];
} else {
  task = raw;
}

if (!task || !task.entity_type) {
  throw new Error('Invalid task: missing entity_type');
}

// Build prompt
const userPrompt = (task.prompt || '')
  .replace('{message}', task.input_message || '')
  .replace('{text}', task.input_message || '');

return {
  task,
  system_prompt: task.system_prompt || 'Извлеки запрошенную информацию из текста.',
  user_prompt: userPrompt,
  chatInput: `${userPrompt}`
};

const task = $('Parse Task').item.json.task;
const aiResponse = $input.item.json;

let result;
let rawOutput = '';
try {
  rawOutput = aiResponse.output || aiResponse.text || JSON.stringify(aiResponse);

  // Убираем markdown code blocks
  rawOutput = rawOutput.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

  // Пробуем распарсить как JSON (объект или массив)
  const jsonMatch = rawOutput.match(/(\{[\s\S]*\}|\[[\s\S]*\])/);
  if (jsonMatch) {
    const parsed = JSON.parse(jsonMatch[0]);

    // Если массив — конвертируем в объект
    if (Array.isArray(parsed)) {
      result = { 
        [task.entity_type]: parsed,
        items: parsed 
      };
    } else {
      result = parsed;
    }
  } else {
    // Try key:value format
    const lines = rawOutput.split('\n');
    result = {};
    for (const line of lines) {
      const match = line.match(/^([\w_]+):\s*(.+)$/i);
      if (match) {
        result[match[1].toLowerCase()] = match[2].trim();
      }
    }
  }
} catch (e) {
  result = { raw: rawOutput, parse_error: e.message };
}

return {
  task,
  result,
  raw_output: rawOutput,
  success: !result.parse_error
};

const result = $('Parse Result').item.json;
const savedId = $input.item.json.id;

return {
  success: true,
  task_id: savedId,
  entity_type: result.task.entity_type,
  appeal_id: result.task.appeal_id,
  result: result.result
};
