const input = $input.first().json.body || $input.first().json;

if (!input.message) {
  throw new Error('Missing required field: message');
}

const schema = input.extraction_schema || {
  entities: ['device', 'symptom', 'owner', 'intent'],
  multi_entity: true
};

// Build extraction prompt
const systemPrompt = `You are an entity extraction system for a phone repair service.
Extract entities from user messages and return ONLY valid JSON.

Entity types to extract:
- device: {type: "device", brand: string, model: string, color?: string}
- symptom: {type: "symptom", text: string, for_device?: string}
- owner: {type: "owner", label: string} (e.g., "my", "wife's", "kid's")
- intent: {type: "intent", value: string} (e.g., "repair", "price_check", "book", "cancel")

RULES:
1. Return ONLY JSON array, no explanations
2. Extract ALL mentioned devices if multi_entity is true
3. Brand normalization: "айфон/iphone" → "Apple", "самсунг/samsung" → "Samsung", "сяоми/xiaomi" → "Xiaomi"
4. If device model unclear, extract what you can
5. Empty array [] if nothing to extract`;

const userPrompt = `Extract entities from: "${input.message}"

Schema: ${JSON.stringify(schema)}

Return JSON array:`;

return {
  message: input.message,
  system_prompt: systemPrompt,
  user_prompt: userPrompt,
  schema: schema,
  trace_id: input.trace_id || `trace_${Date.now()}`
};

