const input = $input.first().json.body || $input.first().json;

// Validate required fields
const required = ['tenant_id', 'client_id', 'dialog_id', 'text'];
for (const field of required) {
  if (!input[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Prepare input for AI Agent
return {
  input: input,
  agent_input: `Collect context for this message:

Message: "${input.text}"
Client ID: ${input.client_id}
Dialog ID: ${input.dialog_id}
Tenant ID: ${input.tenant_id}
Vertical ID: ${input.vertical_id || 1}
Channel: ${input.channel_id || 'unknown'}

Tasks:
1. Extract entities (device, symptom, intent, owner) from the message
2. Load existing client context if available
3. If symptom found - derive diagnosis, repair type and price
4. Check for applicable triggers

Return complete context object.`
};

const agentOutput = $input.first().json;
const inputData = $('Prepare Input').first().json.input;

// Parse agent output and build final context
let context = {
  tenant_id: inputData.tenant_id,
  client_id: inputData.client_id,
  dialog_id: inputData.dialog_id,
  vertical_id: inputData.vertical_id || 1,
  channel_id: inputData.channel_id,
  external_chat_id: inputData.external_chat_id,

  message: {
    text: inputData.text,
    timestamp: inputData.timestamp || new Date().toISOString()
  },

  // From agent
  entities: agentOutput.entities || [],
  client: agentOutput.client || {},
  derived: agentOutput.derived || {},
  triggers_fired: agentOutput.triggers_fired || [],

  // Lines structure
  lines: [],
  focus_line_id: null,

  // Stage (default)
  current_stage: 'data_collection',

  trace_id: inputData.trace_id || `trace_${Date.now()}`
};

// Build lines from entities
const devices = context.entities.filter(e => e.type === 'device');
const symptoms = context.entities.filter(e => e.type === 'symptom');

devices.forEach((device, i) => {
  const symptom = symptoms[i] || symptoms[0];
  context.lines.push({
    id: `line_${i}`,
    status: i === 0 ? 'active' : 'waiting',
    cursor: symptom ? 2 : 1,
    slots: {
      device: device,
      symptom: symptom?.text || null,
      diagnosis: context.derived?.diagnosis || null,
      repair_type: context.derived?.repair_type || null,
      price: context.derived?.price || null
    }
  });
});

if (context.lines.length > 0) {
  context.focus_line_id = 'line_0';
}

return { context };
