const input = $input.first().json;
const context = input.context;
const writeMode = input.write_mode || 'incremental';

// Validate required fields
if (!context) {
  throw new Error('Missing required field: context');
}

const clientId = context.client_id;
const dialogId = context.dialog_id;
const tenantId = context.tenant_id;

if (!clientId) {
  throw new Error('Missing required field: context.client_id');
}

// Prepare data for Neo4j writes
const writes = {
  client: {
    id: clientId,
    name: context.client?.name || null,
    phone: context.client?.phone || null,
    tenant_id: tenantId
  },
  devices: [],
  problems: [],
  touchpoint: {
    dialog_id: dialogId,
    timestamp: new Date().toISOString(),
    stage: context.current_stage,
    channel_id: context.channel_id
  }
};

// Extract devices and problems from lines
if (context.lines && context.lines.length > 0) {
  for (const line of context.lines) {
    const device = line.slots?.device;
    if (device) {
      writes.devices.push({
        line_id: line.id,
        brand: device.brand,
        model: device.model,
        owner_label: device.owner_label || 'main'
      });
    }

    // Problem from symptom/diagnosis
    const symptom = line.slots?.symptom;
    const diagnosis = line.slots?.diagnosis;
    const repair = line.slots?.repair_type;

    if (symptom || diagnosis) {
      writes.problems.push({
        line_id: line.id,
        symptom_text: symptom?.text,
        symptom_type_code: line.slots?.symptom_type?.code,
        diagnosis_code: diagnosis?.code,
        repair_code: repair?.code,
        price: line.slots?.price?.amount,
        status: line.status === 'done' ? 'quoted' : 'collecting_info'
      });
    }
  }
}

return {
  context: context,
  writes: writes,
  write_mode: writeMode,
  trace_id: context.trace_id
};

