const prepareData = $('Prepare Writes').first().json;
const devices = prepareData.writes.devices;

if (!devices || devices.length === 0) {
  return { skip: true, devices_written: 0 };
}

// Build batch MERGE statements for devices
const statements = devices.map((device, idx) => ({
  statement: `
    MATCH (c:Client {id: $clientId})
    MERGE (d:Device {
      client_id: $clientId,
      brand: $brand,
      model: $model,
      owner_label: $ownerLabel
    })
    ON CREATE SET
      d.id = randomUUID(),
      d.created_at = datetime()
    ON MATCH SET
      d.updated_at = datetime()
    MERGE (c)-[:OWNS]->(d)
    RETURN d.id as device_id, d.brand as brand, d.model as model
  `,
  parameters: {
    clientId: prepareData.writes.client.id,
    brand: device.brand,
    model: device.model,
    ownerLabel: device.owner_label || 'main'
  }
}));

return {
  skip: false,
  statements: statements,
  client_id: prepareData.writes.client.id
};

const prepareData = $('Prepare Writes').first().json;
const problems = prepareData.writes.problems;
const devices = prepareData.writes.devices;

if (!problems || problems.length === 0) {
  return { skip: true, problems_written: 0 };
}

// Build batch statements for problems
const statements = problems.map((problem, idx) => {
  // Find corresponding device
  const device = devices.find(d => d.line_id === problem.line_id);

  return {
    statement: `
      MATCH (c:Client {id: $clientId})-[:OWNS]->(d:Device)
      WHERE d.brand = $brand AND d.model = $model
      MERGE (p:Problem {
        device_id: d.id,
        symptom_text: $symptomText
      })
      ON CREATE SET
        p.id = randomUUID(),
        p.created_at = datetime(),
        p.status = $status
      ON MATCH SET
        p.updated_at = datetime()
      SET
        p.symptom_type_code = $symptomTypeCode,
        p.diagnosis_code = $diagnosisCode,
        p.repair_code = $repairCode,
        p.price = $price,
        p.status = $status
      MERGE (d)-[:HAS_PROBLEM]->(p)
      WITH p
      OPTIONAL MATCH (pt:ProblemType {code: $symptomTypeCode})
      FOREACH (_ IN CASE WHEN pt IS NOT NULL THEN [1] ELSE [] END |
        MERGE (p)-[:OF_TYPE]->(pt)
      )
      RETURN p.id as problem_id, p.status as status
    `,
    parameters: {
      clientId: prepareData.writes.client.id,
      brand: device?.brand || 'Unknown',
      model: device?.model || 'Unknown',
      symptomText: problem.symptom_text || 'Unknown issue',
      symptomTypeCode: problem.symptom_type_code,
      diagnosisCode: problem.diagnosis_code,
      repairCode: problem.repair_code,
      price: problem.price,
      status: problem.status
    }
  };
});

return {
  skip: false,
  statements: statements
};

const prepareData = $('Prepare Writes').first().json;
const touchpoint = prepareData.writes.touchpoint;
const clientId = prepareData.writes.client.id;
const devices = prepareData.writes.devices;

// Create touchpoint for this interaction
const statements = [{
  statement: `
    MATCH (c:Client {id: $clientId})
    CREATE (t:Touchpoint {
      id: randomUUID(),
      dialog_id: $dialogId,
      timestamp: datetime($timestamp),
      stage: $stage,
      channel_id: $channelId
    })
    MERGE (c)-[:HAD_TOUCHPOINT]->(t)
    WITH t
    UNWIND $deviceBrands as deviceBrand
    OPTIONAL MATCH (c)-[:OWNS]->(d:Device {brand: deviceBrand.brand, model: deviceBrand.model})
    FOREACH (_ IN CASE WHEN d IS NOT NULL THEN [1] ELSE [] END |
      MERGE (t)-[:ABOUT_DEVICE]->(d)
    )
    RETURN t.id as touchpoint_id
  `,
  parameters: {
    clientId: clientId,
    dialogId: touchpoint.dialog_id,
    timestamp: touchpoint.timestamp,
    stage: touchpoint.stage,
    channelId: touchpoint.channel_id,
    deviceBrands: devices.map(d => ({ brand: d.brand, model: d.model }))
  }
}];

return { statements: statements };

const prepareData = $('Prepare Writes').first().json;
const devicesWritten = $('Prepare Devices').first()?.json?.skip === false ? 
  prepareData.writes.devices.length : 0;
const problemsWritten = $('Prepare Problems').first()?.json?.skip === false ?
  prepareData.writes.problems.length : 0;

return {
  success: true,
  writes: {
    client: 1,
    devices: devicesWritten,
    problems: problemsWritten,
    touchpoint: 1
  },
  trace_id: prepareData.trace_id
};
