const webhookData = $input.first().json;
const msg = webhookData.payload?.value;

if (!msg) {
  throw new Error('Invalid Avito webhook payload');
}

// AUTH CHECK - простая проверка что это от Avito
if (!webhookData.id || !webhookData.timestamp) {
  throw new Error('Invalid webhook format');
}

// SYSTEM FILTER - пропускаем системные сообщения
const isSystem = (
  msg.author_id === msg.user_id ||
  msg.author_id === 0 ||
  !msg.author_id
);

if (isSystem) {
  return { skip: true, reason: 'system_message' };
}

const messageText = msg.content?.text || '';
const authorName = msg.author?.name || null;

// Проверяем типы медиа
const hasPhoto = msg.type === 'image';
const hasVoice = msg.type === 'voice';
const hasVideo = msg.type === 'video';
const hasDocument = msg.type === 'file';

const urls = [];
let photoUrl = null;
let voiceUrl = null;
let videoUrl = null;

if (msg.content?.url) {
  urls.push(msg.content.url);
  if (hasPhoto) photoUrl = msg.content.url;
  if (hasVoice) voiceUrl = msg.content.url;
  if (hasVideo) videoUrl = msg.content.url;
}

return {
  skip: false,
  has_voice: hasVoice,
  voice_url: voiceUrl,
  raw_message: msg,
  user_id: msg.user_id,
  author_id: msg.author_id,
  author_name: authorName,
  chat_id: msg.chat_id,
  message_id: msg.id,
  message_text: messageText,
  message_type: msg.type,
  created: msg.created,
  item_id: msg.item_id,
  has_photo: hasPhoto,
  photo_url: photoUrl,
  has_video: hasVideo,
  video_url: videoUrl,
  has_document: hasDocument,
  urls: urls
};

const data = $('Parse Auth Filter').first().json;
const transcription = $input.first().json.text;

let messageText = data.message_text;
if (transcription) {
  messageText = (messageText ? messageText + '\n\n' : '') + transcription;
}

return {
  channel: 'avito',
  external_user_id: data.author_id.toString(),
  external_chat_id: data.chat_id,

  text: messageText,
  timestamp: new Date(data.created * 1000).toISOString(),

  client_phone: null,
  client_name: data.author_name,
  client_email: null,

  media: {
    has_voice: true,
    voice_transcribed_text: transcription,
    has_images: data.has_photo,
    images: data.has_photo ? [{ url: data.photo_url }] : [],
    has_video: data.has_video,
    videos: data.has_video ? [{ url: data.video_url }] : [],
    has_document: data.has_document
  },

  meta: {
    external_message_id: data.message_id,
    ad_channel: 'avito',
    ad_id: data.item_id?.toString(),
    original_media_type: 'voice',
    raw: data.raw_message,
    chat_type: data.raw_message.chat_type,
    user_id: data.user_id
  },

  prefilled_data: {
    model: null,
    parts_owner: null
  }
};

const data = $input.first().json;

return {
  channel: 'avito',
  external_user_id: data.author_id.toString(),
  external_chat_id: data.chat_id,

  text: data.message_text,
  timestamp: new Date(data.created * 1000).toISOString(),

  client_phone: null,
  client_name: data.author_name,
  client_email: null,

  media: {
    has_voice: false,
    voice_transcribed_text: null,
    has_images: data.has_photo,
    images: data.has_photo ? [{ url: data.photo_url }] : [],
    has_video: data.has_video,
    videos: data.has_video ? [{ url: data.video_url }] : [],
    has_document: data.has_document
  },

  meta: {
    external_message_id: data.message_id,
    ad_channel: 'avito',
    ad_id: data.item_id?.toString(),
    original_media_type: data.has_photo ? 'photo' : data.has_video ? 'video' : 'text',
    raw: data.raw_message,
    chat_type: data.raw_message.chat_type,
    user_id: data.user_id
  },

  prefilled_data: {
    model: null,
    parts_owner: null
  }
};

// Подготавливаем сообщение для Redis очереди
const data = $input.first().json;

// Сериализуем в JSON для Redis
return {
  message_json: JSON.stringify(data),
  batch_key: `${data.channel}:${data.external_chat_id}`
};
