const formData = $input.first().json;

// Извлекаем данные из формы/квиза
// Поддерживаем различные варианты названий полей
const phone = formData.phone || formData.telephone || formData.mobile || formData.cell || null;
const name = formData.name || formData.full_name || formData.client_name || formData.customer_name || null;
const phoneModel = formData.phone_model || formData.model || formData.device_model || formData.device || null;
const message = formData.message || formData.comment || formData.description || formData.text || '';

// Дополнительные поля
const email = formData.email || null;
const source = formData.source || formData.utm_source || formData.form_source || 'form';
const formId = formData.form_id || formData.quiz_id || formData.id || null;
const formName = formData.form_name || formData.quiz_name || null;

// Формируем текст сообщения
let messageText = '';

// Добавляем модель телефона если указана
if (phoneModel) {
  messageText += `Модель: ${phoneModel}\\n`;
}

// Добавляем основное сообщение
if (message) {
  messageText += message;
}

// Если текста совсем нет
if (!messageText.trim()) {
  messageText = '[Заявка с формы без текста]';
}

// Очищаем и нормализуем телефон
let cleanPhone = null;
if (phone) {
  cleanPhone = phone.replace(/\\D/g, '');

  // Если начинается с 8, меняем на 7
  if (cleanPhone.length === 11 && cleanPhone.startsWith('8')) {
    cleanPhone = '7' + cleanPhone.substring(1);
  }

  // Если начинается с 9 (без кода страны), добавляем 7
  if (cleanPhone.length === 10 && cleanPhone.startsWith('9')) {
    cleanPhone = '7' + cleanPhone;
  }

  // Добавляем +
  if (cleanPhone) {
    cleanPhone = '+' + cleanPhone;
  }
}

return {
  channel: 'form',
  external_user_id: cleanPhone?.replace(/\\+/g, '') || email || 'unknown',
  external_chat_id: cleanPhone || email || 'unknown',

  text: messageText.trim(),
  timestamp: new Date().toISOString(),

  client_phone: cleanPhone,
  client_name: name,
  client_email: email,

  media: {
    has_voice: false,
    voice_transcribed_text: null,
    has_images: false,
    images: [],
    has_video: false,
    videos: [],
    has_document: false
  },

  meta: {
    external_message_id: formId || Date.now().toString(),
    ad_channel: 'form',
    ad_id: null,
    original_media_type: 'text',
    raw: formData,
    form_source: source,
    form_id: formId,
    form_name: formName
  },

  prefilled_data: {
    model: phoneModel,
    parts_owner: null
  }
};
