const input = $input.first().json;
const event = input.body || input;

// Detect provider: Baileys or WAPPI
const isBaileys = event.event === 'message' && event.data;
const data = isBaileys ? event.data : event;

// Extract fields based on provider
let chatId, phone, messageText, messageId, timestamp, senderName;
let hasPhoto = false, hasVoice = false, hasVideo = false, hasDocument = false;
let photoUrl = null, voiceUrl = null, videoUrl = null;
const urls = [];

if (isBaileys) {
  // Baileys format
  chatId = data.from + '@s.whatsapp.net';
  phone = '+' + data.from;
  messageText = data.text || data.caption || '';
  messageId = data.messageId;
  timestamp = data.timestamp ? new Date(data.timestamp).toISOString() : new Date().toISOString();
  senderName = data.fromName;

  // Media types
  const msgType = data.type;
  hasPhoto = msgType === 'image';
  hasVoice = msgType === 'audio';
  hasVideo = msgType === 'video';
  hasDocument = msgType === 'document';

  if (data.mediaUrl) {
    urls.push(data.mediaUrl);
    if (hasPhoto) photoUrl = data.mediaUrl;
    if (hasVoice) voiceUrl = data.mediaUrl;
    if (hasVideo) videoUrl = data.mediaUrl;
  }
} else {
  // WAPPI format (legacy)
  chatId = event.chatId || event.chat_id;
  const sender = event.sender || event.from;
  messageText = event.text || event.body || '';
  messageId = event.messageId || event.id || Date.now().toString();
  timestamp = event.timestamp ? new Date(event.timestamp * 1000).toISOString() : new Date().toISOString();
  phone = chatId ? chatId.split('@')[0] : sender;
  if (phone && !phone.startsWith('+')) phone = '+' + phone;
  senderName = event.senderName || event.pushname || null;

  const media = event.media || {};
  hasPhoto = media.type === 'image' || event.media?.mimetype?.startsWith('image/');
  hasVoice = media.type === 'audio' || media.type === 'voice' || event.media?.mimetype?.startsWith('audio/');
  hasVideo = media.type === 'video' || event.media?.mimetype?.startsWith('video/');
  hasDocument = media.type === 'document' || event.media?.mimetype === 'application/pdf';

  if (media.url) {
    urls.push(media.url);
    if (hasPhoto) photoUrl = media.url;
    if (hasVoice) voiceUrl = media.url;
    if (hasVideo) videoUrl = media.url;
  }
}

// Extract session_id for Baileys (used as profile_id for tenant lookup)
const sessionId = event.sessionId || data.sessionId || 'eldoleado_main';

return {
  provider: isBaileys ? 'baileys' : 'wappi',
  session_id: sessionId,
  has_voice: hasVoice,
  voice_url: voiceUrl,
  raw_event: event,
  chat_id: chatId,
  phone: phone,
  message_text: messageText,
  message_id: messageId,
  timestamp: timestamp,
  sender_name: senderName,
  has_photo: hasPhoto,
  photo_url: photoUrl,
  has_video: hasVideo,
  video_url: videoUrl,
  has_document: hasDocument,
  urls: urls
};

const data = $('Extract WhatsApp Data').first().json;
const transcription = $input.first().json.text;

let messageText = data.message_text;
if (transcription) {
  messageText = (messageText ? messageText + '\n\n' : '') + transcription;
}

return {
  channel: 'whatsapp',
  profile_id: data.session_id,
  external_user_id: data.phone?.replace(/\+/g, ''),
  external_chat_id: data.chat_id,

  text: messageText,
  timestamp: data.timestamp,

  client_phone: data.phone,
  client_name: data.sender_name,
  client_email: null,

  media: {
    has_voice: true,
    voice_transcribed_text: transcription,
    has_images: data.has_photo,
    images: data.has_photo ? [{ url: data.photo_url }] : [],
    has_video: data.has_video,
    videos: data.has_video ? [{ url: data.video_url }] : [],
    has_document: data.has_document
  },

  meta: {
    external_message_id: data.message_id,
    ad_channel: 'whatsapp',
    ad_id: null,
    original_media_type: 'voice',
    raw: data.raw_event,
    provider: data.provider || 'baileys'
  },

  prefilled_data: {
    model: null,
    parts_owner: null
  }
};

const data = $input.first().json;

return {
  channel: 'whatsapp',
  profile_id: data.session_id,
  external_user_id: data.phone?.replace(/\+/g, ''),
  external_chat_id: data.chat_id,

  text: data.message_text,
  timestamp: data.timestamp,

  client_phone: data.phone,
  client_name: data.sender_name,
  client_email: null,

  media: {
    has_voice: false,
    voice_transcribed_text: null,
    has_images: data.has_photo,
    images: data.has_photo ? [{ url: data.photo_url }] : [],
    has_video: data.has_video,
    videos: data.has_video ? [{ url: data.video_url }] : [],
    has_document: data.has_document
  },

  meta: {
    external_message_id: data.message_id,
    ad_channel: 'whatsapp',
    ad_id: null,
    original_media_type: data.has_photo ? 'photo' : data.has_video ? 'video' : 'text',
    raw: data.raw_event,
    provider: data.provider || 'baileys'
  },

  prefilled_data: {
    model: null,
    parts_owner: null
  }
};

// Prepare message for Redis queue (batcher will handle DB writes)
const data = $input.first().json;

return {
  message_json: JSON.stringify(data),
  batch_key: `${data.channel}:${data.external_chat_id}`
};
