// ELO_Unifier - Validate Input & Normalize Phone
// Input: tenant_id, client_id, phone, source

const input = $input.first().json;

// Required fields
if (!input.tenant_id) throw new Error('Missing tenant_id');
if (!input.client_id) throw new Error('Missing client_id');
if (!input.phone) throw new Error('Missing phone');

// Normalize phone (Russian format)
function normalizePhone(phone) {
  // Remove all non-digits
  let digits = phone.replace(/\D/g, '');

  // Handle Russian formats
  if (digits.length === 11) {
    // 89991234567 → 79991234567
    if (digits.startsWith('8')) {
      digits = '7' + digits.slice(1);
    }
  } else if (digits.length === 10) {
    // 9991234567 → 79991234567
    digits = '7' + digits;
  }

  // Return with + prefix
  return '+' + digits;
}

const normalizedPhone = normalizePhone(input.phone);

// Validate phone length (should be 11-15 digits)
const phoneDigits = normalizedPhone.replace(/\D/g, '');
if (phoneDigits.length < 10 || phoneDigits.length > 15) {
  throw new Error(`Invalid phone format: ${input.phone}`);
}

// Generate trace_id
const trace_id = input.trace_id || `unify_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

return {
  tenant_id: input.tenant_id,
  client_id: input.client_id,
  original_phone: input.phone,
  phone: normalizedPhone,
  source: input.source || 'unknown',  // resolver, ai, manual
  trace_id,
  started_at: new Date().toISOString()
};

// Check if current client already has this phone
const input = $('Validate & Normalize').first().json;
const currentClient = $json;

if (!currentClient.id) {
  throw new Error(`Client not found: ${input.client_id}`);
}

// If current client already has this phone - nothing to do
if (currentClient.phone === input.phone) {
  return {
    ...input,
    action: 'none',
    reason: 'Client already has this phone',
    result_client_id: input.client_id,
    was_merged: false
  };
}

// Need to search for other clients with this phone
return {
  ...input,
  current_client_phone: currentClient.phone,
  action: 'search'
};

// Prepare merge operation
const input = $('Check Current Client').first().json;
const existing = $json;

return {
  ...input,
  existing_client_id: existing.existing_client_id,
  existing_client_name: existing.name,
  action: 'merge'
};

// Merge completed
const input = $('Prepare Merge').first().json;

return {
  ...input,
  result_client_id: input.existing_client_id,
  was_merged: true,
  merged_from: input.client_id,
  merged_to: input.existing_client_id,
  action: 'merged'
};

