UPDATE elo_t_clients
SET phone = '{{ $('Validate & Normalize').first().json.phone }}',
    updated_at = NOW()
WHERE id = '{{ $('Validate & Normalize').first().json.client_id }}';

SELECT '{{ $('Validate & Normalize').first().json.client_id }}' as result_client_id, false as was_merged;

