// Валидация входных данных для синхронизации PostgreSQL → Neo4j
const ALLOWED_ENTITY_TYPES = ['client', 'device', 'problem', 'channel'];
const ALLOWED_OPERATIONS = ['create', 'update', 'delete'];

const body = $json.body || {};
const entity_type = body.entity_type;
const operation = body.operation;
const data = body.data || {};
const parent_id = body.parent_id || null; // для device → client_id, для problem → device_id

// Валидация
if (!ALLOWED_ENTITY_TYPES.includes(entity_type)) {
  throw new Error(`Invalid entity_type: ${entity_type}. Allowed: ${ALLOWED_ENTITY_TYPES.join(', ')}`);
}

if (!ALLOWED_OPERATIONS.includes(operation)) {
  throw new Error(`Invalid operation: ${operation}. Allowed: ${ALLOWED_OPERATIONS.join(', ')}`);
}

if (!data.id && operation !== 'delete') {
  throw new Error('data.id is required for create/update operations');
}

// Маппинг entity_type → nodeType
const NODE_TYPE_MAP = {
  'client': 'Client',
  'device': 'Device',
  'problem': 'Problem',
  'channel': 'Channel'
};

// Маппинг для рёбер при создании
const EDGE_MAP = {
  'device': { edgeType: 'OWNS', parentType: 'Client' },
  'problem': { edgeType: 'HAS_PROBLEM', parentType: 'Device' },
  'channel': { edgeType: 'HAS_CHANNEL', parentType: 'Client' }
};

const nodeType = NODE_TYPE_MAP[entity_type];
const edgeInfo = EDGE_MAP[entity_type] || null;

// Санитизация ключей data
const sanitizedData = {};
for (const [key, value] of Object.entries(data)) {
  if (!/^[a-zA-Z_][a-zA-Z0-9_]*$/.test(key)) {
    throw new Error(`Invalid data key: ${key}`);
  }
  sanitizedData[key] = value;
}

return {
  entity_type,
  operation,
  nodeType,
  data: sanitizedData,
  parent_id,
  edgeInfo,
  _validation: 'passed'
};

parameters.jsonBody:
={{ 
  const nodeType = $json.nodeType;
  const data = $json.data;
  const parent_id = $json.parent_id;
  const edgeInfo = $json.edgeInfo;

  // Формируем свойства для CREATE
  const propsStr = Object.entries(data).map(([k,v]) => `${k}: $${k}`).join(', ');

  const statements = [];

  // 1. Создаём узел
  statements.push({
    statement: `CREATE (n:${nodeType} {${propsStr}}) RETURN n`,
    parameters: data
  });

  // 2. Если есть parent_id, создаём связь
  if (parent_id && edgeInfo) {
    statements.push({
      statement: `MATCH (parent:${edgeInfo.parentType} {id: $parentId}), (child:${nodeType} {id: $childId}) CREATE (parent)-[:${edgeInfo.edgeType}]->(child) RETURN parent, child`,
      parameters: { parentId: parent_id, childId: data.id }
    });
  }

  JSON.stringify({ statements })
}}

={{ 
  const nodeType = $json.nodeType;
  const data = $json.data;
  const nodeId = data.id;

  // Формируем SET для UPDATE (исключаем id)
  const updateProps = Object.entries(data)
    .filter(([k]) => k !== 'id')
    .map(([k,v]) => `n.${k} = $${k}`);

  const setStr = updateProps.join(', ');

  JSON.stringify({
    statements: [{
      statement: setStr 
        ? `MATCH (n:${nodeType} {id: $id}) SET ${setStr} RETURN n`
        : `MATCH (n:${nodeType} {id: $id}) RETURN n`,
      parameters: data
    }]
  })
}}

={{ 
  const nodeType = $json.nodeType;
  const nodeId = $json.data.id;

  JSON.stringify({
    statements: [{
      statement: `MATCH (n:${nodeType} {id: $id}) DETACH DELETE n RETURN count(n) as deleted`,
      parameters: { id: nodeId }
    }]
  })
}}

parameters.jsCode:
const result = $json;

// Проверка на ошибки Neo4j
if (result.errors && result.errors.length > 0) {
  const errorMessages = result.errors.map(e => e.message).join('; ');
  return {
    success: false,
    error: errorMessages
  };
}

const data = result.results?.[0]?.data || [];

return {
  success: true,
  synced: data.length > 0,
  results: data.map(d => d.row?.[0] || d)
};
