// Анализ входящего сообщения на disambiguation
const data = $input.first().json;
const message = data.message_text || data.text || '';
const devices = data.devices || [];
const ambiguousDevices = data.ambiguous_devices || [];

// Паттерны для распознавания устройства
const patterns = {
  // Порядковые номера
  first: /перв(ый|ое|ая|ого)|первый телефон|1-?(й|ый|ое)|номер 1|#1/i,
  second: /втор(ой|ое|ая|ого)|второй телефон|2-?(й|ой|ое)|номер 2|#2/i,
  third: /трет(ий|ье|ья|ьего)|третий телефон|3-?(й|ий|ье)|номер 3|#3/i,

  // Владелец
  my: /мо(й|ё|я|его|им)|свой|личн(ый|ое|ая)/i,
  son: /сын(а|у|ом)?|ребёнк(а|у|ом)?|сыночк/i,
  wife: /жен(ы|е|ой|у)?|супруг(и|е|ой)?/i,
  husband: /муж(а|у|ем)?|супруг(а|у|ом)?/i,
  mother: /мам(ы|е|ой|у)?|матер(и|ью)?/i,
  father: /пап(ы|е|ой|у)?|отц(а|у|ом)?/i,

  // По проблеме
  broken_screen: /экран|дисплей|стекл(о|а)|разбит|треснул/i,
  battery: /батаре(я|и|ю|ей)|аккумулятор|заряд|разряж/i,
  not_charging: /не заряжа|зарядк(а|у|и)/i
};

let resolvedDeviceId = null;
let resolvedBy = null;
let confidence = 0;

// Попытка распознать по порядковому номеру
if (patterns.first.test(message) && devices.length >= 1) {
  resolvedDeviceId = devices[0].id;
  resolvedBy = 'order_number';
  confidence = 0.9;
} else if (patterns.second.test(message) && devices.length >= 2) {
  resolvedDeviceId = devices[1].id;
  resolvedBy = 'order_number';
  confidence = 0.9;
} else if (patterns.third.test(message) && devices.length >= 3) {
  resolvedDeviceId = devices[2].id;
  resolvedBy = 'order_number';
  confidence = 0.9;
}

// Попытка по владельцу (если есть owner_label)
if (!resolvedDeviceId) {
  for (const device of devices) {
    const ownerLabel = (device.owner_label || '').toLowerCase();

    if (patterns.my.test(message) && (ownerLabel.includes('свой') || ownerLabel.includes('мой') || ownerLabel === '')) {
      resolvedDeviceId = device.id;
      resolvedBy = 'owner_label';
      confidence = 0.7;
      break;
    }
    if (patterns.son.test(message) && ownerLabel.includes('сын')) {
      resolvedDeviceId = device.id;
      resolvedBy = 'owner_label';
      confidence = 0.85;
      break;
    }
    if (patterns.wife.test(message) && (ownerLabel.includes('жен') || ownerLabel.includes('супруг'))) {
      resolvedDeviceId = device.id;
      resolvedBy = 'owner_label';
      confidence = 0.85;
      break;
    }
  }
}

// Попытка по проблеме (если есть repairs)
if (!resolvedDeviceId) {
  for (const device of devices) {
    const repairs = device.repairs || [];
    const repairText = repairs.map(r => r.category_name || r.repair_type_name || '').join(' ').toLowerCase();

    if (patterns.broken_screen.test(message) && (repairText.includes('экран') || repairText.includes('дисплей'))) {
      resolvedDeviceId = device.id;
      resolvedBy = 'repair_match';
      confidence = 0.75;
      break;
    }
    if (patterns.battery.test(message) && repairText.includes('батаре')) {
      resolvedDeviceId = device.id;
      resolvedBy = 'repair_match';
      confidence = 0.75;
      break;
    }
  }
}

return {
  ...data,
  disambiguation: {
    resolved: !!resolvedDeviceId,
    device_id: resolvedDeviceId,
    resolved_by: resolvedBy,
    confidence: confidence,
    original_message: message
  }
};

// Формируем ответ после успешного разрешения
const data = $input.first().json;
const device = Array.isArray(data) ? data[0] : data;
const prev = $('Analyze Message').first().json;

const deviceName = `${device.brand_name || ''} ${device.model_name || ''}`.trim() || 'устройство';
const ownerLabel = device.owner_label ? ` (${device.owner_label})` : '';

return {
  success: true,
  resolved: true,
  device_id: prev.disambiguation.device_id,
  device_name: deviceName,
  owner_label: device.owner_label,
  resolved_by: prev.disambiguation.resolved_by,
  confidence: prev.disambiguation.confidence,
  response_text: `Понял, говорим про ${deviceName}${ownerLabel}. Продолжаем!`,
  // Для дальнейшей обработки
  appeal_id: prev.appeal_id,
  tenant_id: prev.tenant_id,
  channel: prev.channel
};

// Формируем уточняющий вопрос
const data = $input.first().json;
const devices = data.devices || [];

// Строим список устройств для уточнения
const lines = ['Уточните, какое устройство имеете в виду:'];

devices.forEach((d, i) => {
  const brand = d.brand_name || '?';
  const model = d.model_name || '?';
  const owner = d.owner_label ? ` (${d.owner_label})` : '';
  const repairs = (d.repairs || []).map(r => r.category_name || r.repair_type_name).filter(Boolean).join(', ');
  const repairInfo = repairs ? ` — ${repairs}` : '';

  lines.push(`${i + 1}. ${brand} ${model}${owner}${repairInfo}`);
});

lines.push('');
lines.push('Напишите номер или опишите устройство.');

return {
  success: true,
  resolved: false,
  needs_clarification: true,
  response_text: lines.join('\n'),
  devices_count: devices.length,
  // Для дальнейшей обработки
  appeal_id: data.appeal_id,
  tenant_id: data.tenant_id,
  channel: data.channel
};
