// Валидация входных данных для Touchpoint
const ALLOWED_CHANNELS = ['whatsapp', 'telegram', 'vk', 'avito', 'max', 'phone', 'email', 'web', 'in_person'];
const ALLOWED_DIRECTIONS = ['inbound', 'outbound'];
const ALLOWED_TYPES = ['message', 'call', 'visit', 'retargeting', 'email', 'form'];

const body = $json.body || {};

const client_id = body.client_id;
const message_id = body.message_id || crypto.randomUUID();
const channel = body.channel || 'unknown';
const direction = body.direction || 'inbound';
const type = body.type || 'message';
const mentioned_device_id = body.mentioned_device_id || null;
const mentioned_problem_id = body.mentioned_problem_id || null;
const confidence = body.confidence || 1.0;
const explicit = body.explicit !== false; // по умолчанию true

// Валидация обязательных полей
if (!client_id) {
  throw new Error('client_id is required');
}

// Валидация channel (мягкая - пропускаем unknown)
if (channel !== 'unknown' && !ALLOWED_CHANNELS.includes(channel)) {
  console.log(`Warning: Unknown channel type: ${channel}`);
}

if (!ALLOWED_DIRECTIONS.includes(direction)) {
  throw new Error(`Invalid direction: ${direction}. Allowed: ${ALLOWED_DIRECTIONS.join(', ')}`);
}

return {
  client_id,
  message_id,
  channel,
  direction,
  type,
  mentioned_device_id,
  mentioned_problem_id,
  confidence,
  explicit,
  timestamp: new Date().toISOString(),
  _validation: 'passed'
};

parameters.jsonBody:
={{ 
  const data = $json;
  const statements = [];

  // 1. Создаём Touchpoint узел
  statements.push({
    statement: `
      CREATE (t:Touchpoint {
        id: $id,
        timestamp: datetime($timestamp),
        type: $type,
        channel: $channel,
        direction: $direction
      })
      RETURN t
    `,
    parameters: {
      id: data.message_id,
      timestamp: data.timestamp,
      type: data.type,
      channel: data.channel,
      direction: data.direction
    }
  });

  // 2. Связь FROM/TO с Client
  const edgeType = data.direction === 'inbound' ? 'FROM' : 'TO';
  statements.push({
    statement: `
      MATCH (t:Touchpoint {id: $touchpointId})
      MATCH (c:Client {id: $clientId})
      CREATE (t)-[:${edgeType}]->(c)
      RETURN t, c
    `,
    parameters: {
      touchpointId: data.message_id,
      clientId: data.client_id
    }
  });

  // 3. Если упоминается устройство - связь ABOUT_DEVICE
  if (data.mentioned_device_id) {
    statements.push({
      statement: `
        MATCH (t:Touchpoint {id: $touchpointId})
        MATCH (d:Device {id: $deviceId})
        CREATE (t)-[:ABOUT_DEVICE {confidence: $confidence, explicit: $explicit}]->(d)
        RETURN t, d
      `,
      parameters: {
        touchpointId: data.message_id,
        deviceId: data.mentioned_device_id,
        confidence: data.confidence,
        explicit: data.explicit
      }
    });
  }

  // 4. Если упоминается проблема - связь ABOUT_PROBLEM
  if (data.mentioned_problem_id) {
    statements.push({
      statement: `
        MATCH (t:Touchpoint {id: $touchpointId})
        MATCH (p:Problem {id: $problemId})
        CREATE (t)-[:ABOUT_PROBLEM {confidence: $confidence, explicit: $explicit}]->(p)
        RETURN t, p
      `,
      parameters: {
        touchpointId: data.message_id,
        problemId: data.mentioned_problem_id,
        confidence: data.confidence,
        explicit: data.explicit
      }
    });
  }

  JSON.stringify({ statements })
}}

parameters.jsCode:
const result = $json;

// Проверка на ошибки Neo4j
if (result.errors && result.errors.length > 0) {
  const errorMessages = result.errors.map(e => e.message).join('; ');
  return {
    success: false,
    error: errorMessages
  };
}

// Считаем успешные операции
const successfulStatements = result.results?.filter(r => r.data?.length > 0) || [];

return {
  success: true,
  touchpoint_created: successfulStatements.length > 0,
  edges_created: successfulStatements.length - 1 // первый - узел, остальные - рёбра
};
