const event = $input.first().json;

// MAX.ru webhook structure
const msg = event.message || event;
const sender = msg.from || msg.sender;

// Извлекаем текст сообщения
const messageText = msg.text || msg.body || '';

// Определяем типы медиа
const hasPhoto = msg.type === 'image' || !!msg.image;
const hasVoice = msg.type === 'voice' || msg.type === 'audio' || !!msg.voice || !!msg.audio;
const hasVideo = msg.type === 'video' || !!msg.video;
const hasDocument = msg.type === 'document' || !!msg.document;

// Извлекаем URL медиа
const urls = [];
let photoUrl = null;
let voiceUrl = null;
let videoUrl = null;

if (msg.image?.url) {
  photoUrl = msg.image.url;
  urls.push(photoUrl);
}

if (msg.voice?.url || msg.audio?.url) {
  voiceUrl = msg.voice?.url || msg.audio?.url;
  urls.push(voiceUrl);
}

if (msg.video?.url) {
  videoUrl = msg.video.url;
  urls.push(videoUrl);
}

if (msg.document?.url) {
  urls.push(msg.document.url);
}

// Извлекаем данные отправителя
const senderId = sender?.id || sender?.phone || msg.chat_id;
const senderName = sender?.name || sender?.display_name || null;
const senderPhone = sender?.phone || null;

// Нормализуем телефон если есть
let cleanPhone = null;
if (senderPhone) {
  cleanPhone = senderPhone.replace(/\D/g, '');
  if (cleanPhone.length === 11 && cleanPhone.startsWith('8')) {
    cleanPhone = '7' + cleanPhone.substring(1);
  }
  if (cleanPhone) {
    cleanPhone = '+' + cleanPhone;
  }
}

return {
  has_voice: hasVoice,
  voice_url: voiceUrl,
  raw_event: event,
  sender_id: senderId,
  sender_name: senderName,
  sender_phone: cleanPhone,
  chat_id: msg.chat_id || senderId?.toString(),
  message_id: msg.id || msg.message_id || Date.now().toString(),
  message_text: messageText,
  message_type: msg.type,
  timestamp: msg.timestamp ? new Date(msg.timestamp * 1000).toISOString() : new Date().toISOString(),
  has_photo: hasPhoto,
  photo_url: photoUrl,
  has_video: hasVideo,
  video_url: videoUrl,
  has_document: hasDocument,
  urls: urls
};

const data = $('Extract MAX Data').first().json;
const transcription = $input.first().json.text;

let messageText = data.message_text;
if (transcription) {
  messageText = (messageText ? messageText + '\n\n' : '') + transcription;
}

return {
  channel: 'max',
  external_user_id: data.sender_id?.toString(),
  external_chat_id: data.chat_id,

  text: messageText,
  timestamp: data.timestamp,

  client_phone: data.sender_phone,
  client_name: data.sender_name,
  client_email: null,

  media: {
    has_voice: true,
    voice_transcribed_text: transcription,
    has_images: data.has_photo,
    images: data.has_photo ? [{ url: data.photo_url }] : [],
    has_video: data.has_video,
    videos: data.has_video ? [{ url: data.video_url }] : [],
    has_document: data.has_document
  },

  meta: {
    external_message_id: data.message_id,
    ad_channel: 'max',
    ad_id: null,
    original_media_type: 'voice',
    raw: data.raw_event
  },

  prefilled_data: {
    model: null,
    parts_owner: null
  }
};

const data = $input.first().json;

return {
  channel: 'max',
  external_user_id: data.sender_id?.toString(),
  external_chat_id: data.chat_id,

  text: data.message_text,
  timestamp: data.timestamp,

  client_phone: data.sender_phone,
  client_name: data.sender_name,
  client_email: null,

  media: {
    has_voice: false,
    voice_transcribed_text: null,
    has_images: data.has_photo,
    images: data.has_photo ? [{ url: data.photo_url }] : [],
    has_video: data.has_video,
    videos: data.has_video ? [{ url: data.video_url }] : [],
    has_document: data.has_document
  },

  meta: {
    external_message_id: data.message_id,
    ad_channel: 'max',
    ad_id: null,
    original_media_type: data.has_photo ? 'photo' : data.has_video ? 'video' : 'text',
    raw: data.raw_event
  },

  prefilled_data: {
    model: null,
    parts_owner: null
  }
};

// Подготавливаем сообщение для Redis очереди
const data = $input.first().json;

return {
  message_json: JSON.stringify(data),
  batch_key: `${data.channel}:${data.external_chat_id}`
};
