const body = $json.body || {};
const clientId = body.client_id || null;
const action = body.action || 'get_context';

// Для match_entities
const extracted = body.extracted || {};
const brand = extracted.brand || null;
const model = extracted.model || null;
const ownerLabel = extracted.owner_label || null;
const problemType = extracted.problem_type || null;
const currentFocus = body.current_focus || {};

return {
  clientId,
  action,
  extracted: {
    brand,
    model,
    ownerLabel,
    problemType
  },
  currentFocus
};

parameters.jsonBody:
={{ JSON.stringify({
  statements: [{
    statement: `
      MATCH (c:Client {id: $clientId})
      OPTIONAL MATCH (c)-[:OWNS]->(d:Device)
      OPTIONAL MATCH (d)-[:HAS_PROBLEM]->(p:Problem)-[:OF_TYPE]->(pt:ProblemType)
      OPTIONAL MATCH (c)-[:HAS_CHANNEL]->(ch:Channel)
      OPTIONAL MATCH (c)-[:CUSTOMER_OF]->(v:Vertical)
      OPTIONAL MATCH (t:Touchpoint)-[:ABOUT_DEVICE]->(d)
      WHERE t.timestamp > datetime() - duration('P7D')
      RETURN c, 
             collect(DISTINCT {id: d.id, brand: d.brand, model: d.model, owner_label: d.owner_label}) as devices,
             collect(DISTINCT {id: p.id, status: p.status, type: pt.code}) as problems,
             collect(DISTINCT {type: ch.type, identifier: ch.identifier}) as channels,
             collect(DISTINCT v.type) as verticals
    `,
    parameters: { clientId: $json.clientId }
  }]
}) }}

={{ JSON.stringify({
  statements: [{
    statement: `
      MATCH (c:Client {id: $clientId})
      OPTIONAL MATCH (c)-[:HAS_CHANNEL]->(ch:Channel)
      RETURN c.id as clientId,
             collect({type: ch.type, identifier: ch.identifier, verified: ch.verified}) as existingChannels
    `,
    parameters: { clientId: $json.clientId }
  }]
}) }}

parameters.jsCode:
const neo4jResult = $('Neo4j Get Channels').first()?.json || {};
const enrichmentPaths = $('Get Enrichment Paths').all().map(i => i.json);

const data = neo4jResult.results?.[0]?.data?.[0]?.row || [];
const existingChannels = data[1] || [];
const existingTypes = new Set(existingChannels.map(ch => ch.type));

const suggestions = enrichmentPaths
  .filter(path => {
    const hasFrom = existingTypes.has(path.from_channel_type);
    const needsTo = !existingTypes.has(path.to_channel_type);
    return hasFrom && needsTo;
  })
  .slice(0, 3);

return {
  success: true,
  clientId: data[0],
  existingChannels,
  suggestions
};

const result = $json;
const data = result.results?.[0]?.data?.[0]?.row || [];

return {
  success: true,
  client: data[0],
  devices: data[1] || [],
  problems: data[2] || [],
  channels: data[3] || [],
  verticals: data[4] || []
};

const result = $json;
const data = result.results?.[0]?.data || [];

const devices = data.map(d => ({
  id: d.row[0],
  brand: d.row[1],
  model: d.row[2],
  owner_label: d.row[3],
  lastMentioned: d.row[4],
  mentionCount: d.row[5],
  hasActiveRepair: d.row[6]
}));

let needsClarification = false;
let suggestedDevice = null;
let clarificationReason = null;

if (devices.length === 0) {
  needsClarification = false;
} else if (devices.length === 1) {
  suggestedDevice = devices[0];
} else {
  const recentlyMentioned = devices.filter(d => d.lastMentioned);

  if (recentlyMentioned.length === 1) {
    suggestedDevice = recentlyMentioned[0];
  } else if (recentlyMentioned.length > 1) {
    needsClarification = true;
    clarificationReason = 'multiple_mentioned_today';
  } else {
    const withActiveRepair = devices.filter(d => d.hasActiveRepair);
    if (withActiveRepair.length === 1) {
      suggestedDevice = withActiveRepair[0];
    } else {
      needsClarification = true;
      clarificationReason = 'none_mentioned_recently';
    }
  }
}

return {
  success: true,
  devices,
  needsClarification,
  suggestedDevice,
  clarificationReason
};

// Format Match Entities Result
// Определяем: создать новое или использовать существующее

const result = $json;
const inputData = $('Parse Request').first()?.json || {};
const extracted = inputData.extracted || {};
const currentFocus = inputData.currentFocus || {};

const data = result.results?.[0]?.data || [];

// Парсим найденные устройства
const matchedDevices = data.map(d => ({
  deviceId: d.row[0],
  brand: d.row[1],
  model: d.row[2],
  ownerLabel: d.row[3],
  problems: d.row[4] || [],
  lastMentioned: d.row[5],
  mentionCount: d.row[6]
}));

console.log('=== MATCH ENTITIES ===');
console.log('Extracted:', JSON.stringify(extracted));
console.log('Matched devices:', matchedDevices.length);

let deviceAction = 'create_new';  // по умолчанию создаём новое
let matchedDeviceId = null;
let matchedDevice = null;
let problemAction = 'create_new';
let matchedProblemId = null;
let needsClarification = false;
let clarificationReason = null;

// Логика определения device_action
if (!extracted.brand && !extracted.model) {
  // Не извлечено устройство - используем текущий фокус
  if (currentFocus.device_id) {
    deviceAction = 'use_focus';
    matchedDeviceId = currentFocus.device_id;
  } else if (matchedDevices.length === 1) {
    // Только одно устройство у клиента
    deviceAction = 'use_existing';
    matchedDeviceId = matchedDevices[0].deviceId;
    matchedDevice = matchedDevices[0];
  } else if (matchedDevices.length > 1) {
    // Несколько устройств - нужно уточнение
    deviceAction = 'needs_clarification';
    needsClarification = true;
    clarificationReason = 'multiple_devices_no_context';
  }
  // Если нет устройств - оставляем create_new

} else if (matchedDevices.length === 0) {
  // Устройство извлечено, но не найдено в графе
  deviceAction = 'create_new';

} else if (matchedDevices.length === 1) {
  // Найдено ровно одно подходящее устройство
  deviceAction = 'use_existing';
  matchedDeviceId = matchedDevices[0].deviceId;
  matchedDevice = matchedDevices[0];

} else {
  // Найдено несколько подходящих устройств
  // Проверяем - упоминали ли одно из них сегодня
  const recentlyMentioned = matchedDevices.filter(d => d.lastMentioned);

  if (recentlyMentioned.length === 1) {
    deviceAction = 'use_existing';
    matchedDeviceId = recentlyMentioned[0].deviceId;
    matchedDevice = recentlyMentioned[0];
  } else {
    deviceAction = 'needs_clarification';
    needsClarification = true;
    clarificationReason = 'multiple_similar_devices';
  }
}

// Логика определения problem_action
if (extracted.problemType && matchedDevice) {
  const existingProblem = matchedDevice.problems.find(
    p => p.type === extracted.problemType && p.status !== 'closed'
  );

  if (existingProblem) {
    problemAction = 'use_existing';
    matchedProblemId = existingProblem.id;
  } else {
    problemAction = 'create_new';
  }
} else if (!extracted.problemType) {
  problemAction = 'none';  // поломка не извлечена
}

return {
  success: true,

  // Результат матчинга устройства
  device: {
    action: deviceAction,
    matched_id: matchedDeviceId,
    matched_device: matchedDevice,
    all_matches: matchedDevices
  },

  // Результат матчинга поломки
  problem: {
    action: problemAction,
    matched_id: matchedProblemId
  },

  // Нужно ли уточнение
  needs_clarification: needsClarification,
  clarification_reason: clarificationReason,

  // Входные данные для отладки
  extracted,
  current_focus: currentFocus
};
