const params = $json.params || {};
const appeal_id = (params.id || '').trim();

let limit = parseInt($json.query?.limit || '50', 10);
if (!Number.isFinite(limit)) limit = 50;
limit = Math.max(1, Math.min(limit, 200));

return { 
  appeal_id, 
  limit, 
  session_token: $json.session_token 
};

const input = $input.first()?.json;
const row = Array.isArray(input) ? (input[0] || null) : input;
const tenant_id = row && typeof row === 'object' ? (row.tenant_id || null) : null;

const previous = $('Parse Params').first().json;

return {
  ...previous,
  tenant_id
};

const inp = $input.first()?.json;
const row = Array.isArray(inp) ? (inp[0] || null) : inp;

if (!row || typeof row !== "object" || !row.id) {
  return { success: false, error: "not_found" };
}

const rawMessages = Array.isArray(row.messages) ? row.messages : [];
const rawDevices = Array.isArray(row.devices) ? row.devices : [];

const messages = rawMessages.map(m => ({
  text: m.message_text || "",
  sender_type: m.message_type || "client",
  created_at: m.created_at || ""
}));

const devices = rawDevices.map(d => ({
  id: d.id,
  device_order: d.device_order || 1,
  brand_id: d.brand_id || null,
  brand_name: d.brand_name || null,
  model_id: d.model_id || null,
  model_name: d.model_name || d.phone_model || null,
  status: d.status || 'active',
  repairs: (d.repairs || []).map(r => ({
    id: r.id,
    repair_category_id: r.repair_category_id || null,
    repair_category_name: r.repair_category_name || null,
    issue_type_id: r.issue_type_id || null,
    issue_type_name: r.issue_type_name || null,
    parts_owner: r.parts_owner || null,
    status: r.status || 'pending',
    priority: r.priority || 1
  }))
}));

return {
  success: true,
  appeal: {
    id: row.id,
    stage: row.stage,
    operation_mode: row.operation_mode,
    estimated_cost: row.estimated_cost,
    estimated_time: row.estimated_time,
    phone_model: row.phone_model,
    brand_id: row.brand_id,
    model_id: row.model_id,
    repair_type_id: row.repair_type_id,
    repair_type_name: row.repair_type_name,
    parts_owner: row.parts_owner,
    updated_at: row.updated_at,
    ai_response: row.ai_response || null,
  },
  client: {
    name: row.client_name || null,
    phone: row.client_phone || null,
  },
  device: {
    brand: row.brand_name || null,
    model: row.model_name || row.phone_model || null,
  },
  devices: devices,
  messages: messages,
  ai_response: row.ai_response ? { response_text: row.ai_response } : null
};

const headers = $json.headers || {};
let token = null;

const authHeader = headers.authorization || headers.Authorization || '';
if (authHeader.startsWith('Bearer ') || authHeader.startsWith('bearer ')) {
  token = authHeader.substring(7);
}

if (!token) {
  token = headers['x-session-token'] || headers['X-Session-Token'];
}

return { ...($json || {}), session_token: token || null };
