// Prepare context with device disambiguation
const devices = $input.first().json.devices || [];
const appeal = $input.first().json.appeal;
const history = $input.first().json.history || [];

// Group devices by brand+model
const groups = {};
devices.forEach(d => {
  const key = `${d.brand_name || ''} ${d.model_name || ''}`.trim();
  if (!groups[key]) groups[key] = [];
  groups[key].push(d);
});

// Check if disambiguation needed - multiple devices of same type
let needsDisambiguation = false;
let disambiguationInfo = '';

for (const [model, devs] of Object.entries(groups)) {
  if (devs.length > 1) {
    // Multiple same devices - check if we can distinguish by repairs
    const withRepairs = devs.filter(d => d.repairs && d.repairs.length > 0);
    const withoutRepairs = devs.filter(d => !d.repairs || d.repairs.length === 0);

    if (withoutRepairs.length > 1) {
      // Multiple same devices without distinct repairs
      needsDisambiguation = true;
      disambiguationInfo += `–ù–µ—Å–∫–æ–ª—å–∫–æ ${model} (${devs.length} —à—Ç). `;
    }
  }
}

// Build device list for context
const deviceList = devices.map((d, i) => {
  const repairs = d.repairs?.map(r => r.repair_type).join(', ') || '–Ω–µ—Ç —Ä–µ–º–æ–Ω—Ç–æ–≤';
  return `#${i}: ${d.brand_name || '?'} ${d.model_name || '?'} - ${repairs}`;
}).join('
');

return {
  json: {
    appeal,
    devices,
    deviceList,
    history,
    needsDisambiguation,
    disambiguationInfo,
    deviceCount: devices.length
  }
};

