const input = $input.first()?.json || {};
console.log('═══ ATTACH BASE ═══');
console.log('Saving client.id:', input.client?.id);
console.log('client_found:', input.client_found);
return input;

const allItems = $input.all();

console.log('═══ MERGE AFTER FIND1 ═══');
console.log('Total items from Merge:', allItems.length);

let baseData = null;
let sqlResult = null;

for (const item of allItems) {
  const json = item.json;
  if (json?.client?.id) {
    baseData = json;
  }
  if (json?.id && !json?.client) {
    sqlResult = json;
  }
}

if (!baseData) {
  try {
    baseData = $('Attach Base').first()?.json || {};
  } catch (e) {
    baseData = {};
  }
}

let appealRow = null;
let appealFound = false;

if (sqlResult && sqlResult.id) {
  appealRow = sqlResult;
  appealFound = true;
  console.log('✅ Appeal found! ID:', appealRow.id);
} else {
  console.log('⚠️ No active appeal found');
}

const output = {
  ...baseData,
  appeal_found: appealFound
};

if (appealRow) {
  output.appeal = {
    id: appealRow.id,
    stage: appealRow.stage,
    phone_model: appealRow.phone_model,
    model_id: appealRow.model_id,
    brand_id: appealRow.brand_id,
    repair_type_id: appealRow.repair_type_id,
    problem_description: appealRow.problem_description,
    operation_mode: appealRow.operation_mode,
    estimated_cost: appealRow.estimated_cost,
    estimated_time: appealRow.estimated_time,
    parts_owner: appealRow.parts_owner,
    is_new: false
  };
}

console.log('✅ Output client.id:', output.client?.id);
console.log('Appeal found:', output.appeal_found);

return output;

const inputData = $input.first()?.json || {};

console.log('═══ MERGE EXISTING APPEAL ═══');

const existingAppeal = inputData.appeal;

if (!existingAppeal || !existingAppeal.id) {
  throw new Error('Appeal not found in input');
}

console.log('✅ Using existing appeal:', existingAppeal.id);

// Определяем тип touchpoint:
// - Если client_found === false (новый клиент) → было inbound, теперь mutual
// - Если client_found === true (известный клиент) → сразу mutual
const touchpoint_direction = 'mutual'; // Для существующего appeal всегда mutual

return {
  ...inputData,
  appeal: {
    id: existingAppeal.id,
    stage: existingAppeal.stage || 'Первичный контакт',
    phone_model: existingAppeal.phone_model || null,
    model_id: existingAppeal.model_id || null,
    brand_id: existingAppeal.brand_id || null,
    repair_type_id: existingAppeal.repair_type_id || null,
    problem_description: existingAppeal.problem_description || null,
    operation_mode: existingAppeal.operation_mode || 'assist',
    estimated_cost: existingAppeal.estimated_cost || null,
    estimated_time: existingAppeal.estimated_time || null,
    parts_owner: existingAppeal.parts_owner || null,
    is_new: false
  },
  touchpoint_direction: touchpoint_direction
};

function fromNode(name) {
  try {
    const arr = $items(name);
    return (arr && arr.length > 0) ? (arr[0].json || null) : null;
  } catch (e) {
    return null;
  }
}

const inMessage = fromNode('Execute Workflow Trigger') || $input.first()?.json || {};
const sqlResult = $input.first()?.json;

function pick(existing, ...cands) {
  if (existing !== undefined && existing !== null && `${existing}` !== '') return existing;
  for (const c of cands) {
    if (c !== undefined && c !== null && `${c}` !== '') return c;
  }
  return existing ?? null;
}

let appealRow = sqlResult;
if (Array.isArray(sqlResult)) {
  appealRow = sqlResult[0];
}

if (!appealRow || !appealRow.id) {
  throw new Error('No appeal data returned from SQL');
}

const src = inMessage;
const prop = src.propertyName || {};
const meta = prop.meta || {};
const raw = meta.raw || {};

const channel = pick(src.channel, prop.channel, meta.ad_channel);
const external_chat_id = pick(src.external_chat_id, prop.external_chat_id, raw.chat?.id?.toString());
const external_user_id = pick(src.external_user_id, prop.external_user_id, raw.from?.id?.toString());
const external_message_id = pick(src.external_message_id, prop.external_message_id, meta.external_message_id, raw.message_id?.toString());
const timestamp = pick(src.timestamp, prop.timestamp, meta.timestamp, raw.date ? new Date(raw.date * 1000).toISOString() : null);
const text = pick(src.text, prop.text, raw.text);

const client = src.client || {};

const appeal = {
  id: appealRow.id,
  stage: appealRow.stage || 'Первичный контакт',
  phone_model: appealRow.phone_model || null,
  model_id: appealRow.model_id || null,
  brand_id: appealRow.brand_id || null,
  repair_type_id: appealRow.repair_type_id || null,
  problem_description: appealRow.problem_description || null,
  operation_mode: appealRow.operation_mode || 'assist',
  estimated_cost: appealRow.estimated_cost || null,
  estimated_time: appealRow.estimated_time || null,
  parts_owner: appealRow.parts_owner || null,
  is_new: true
};

// Определяем тип touchpoint для НОВОГО appeal:
// - Если client_found === false (новый клиент) → inbound
// - Если client_found === true (известный клиент) → mutual
const client_found = src.client_found || false;
const touchpoint_direction = client_found ? 'mutual' : 'inbound';

console.log('✅ New Appeal created, touchpoint_direction:', touchpoint_direction);
console.log('client_found:', client_found);

const out = {
  ...src,
  ...(channel != null ? { channel } : {}),
  ...(external_chat_id != null ? { external_chat_id } : {}),
  ...(external_user_id != null ? { external_user_id } : {}),
  ...(external_message_id != null ? { external_message_id } : {}),
  ...(timestamp != null ? { timestamp } : {}),
  ...(text ? { text } : {}),
  client: client,
  appeal: appeal,
  touchpoint_direction: touchpoint_direction
};

if (out.success === undefined) out.success = true;

return out;

function fromNode(name) {
  try {
    const arr = $items(name);
    return (arr && arr.length > 0) ? (arr[0].json || null) : null;
  } catch (e) {
    return null;
  }
}

const input = $input.first()?.json || {};

const src =
  fromNode('Merge Existing Appeal') ??
  fromNode('Merge New Appeal') ??
  fromNode('Execute Workflow Trigger') ??
  input;

function pick(existing, ...cands) {
  if (existing !== undefined && existing !== null && `${existing}` !== '') return existing;
  for (const c of cands) {
    if (c !== undefined && c !== null && `${c}` !== '') return c;
  }
  return existing ?? null;
}

const prop = src.propertyName || {};
const meta = prop.meta || {};
const raw  = meta.raw || {};

const channel              = pick(src.channel,              prop.channel,              meta.ad_channel);
const external_chat_id     = pick(src.external_chat_id,     prop.external_chat_id,     raw.chat?.id?.toString());
const external_user_id     = pick(src.external_user_id,     prop.external_user_id,     raw.from?.id?.toString());
const external_message_id  = pick(src.external_message_id,  prop.external_message_id,  meta.external_message_id, raw.message_id?.toString());
const timestamp            = pick(src.timestamp,            prop.timestamp,            meta.timestamp, raw.date ? new Date(raw.date * 1000).toISOString() : null);

let text = (src.text ?? '').trim();
if (!text) text = prop.text || raw.text || '';

const clientSrc = src.client || {};
const client = { ...clientSrc };

const appealSrc = src.appeal || {};
const appealIn = input;

let appeal = null;
const appealId = pick(
  appealSrc.id,
  appealIn.appeal_id,
  appealIn.id
);

if (appealId) {
  appeal = {
    id: appealId,
    stage: pick(appealSrc.stage, appealIn.stage),
    phone_model: pick(appealSrc.phone_model, appealIn.phone_model),
    model_id: pick(appealSrc.model_id, appealIn.model_id),
    brand_id: pick(appealSrc.brand_id, appealIn.brand_id),
    repair_type_id: pick(appealSrc.repair_type_id, appealIn.repair_type_id),
    problem_description: pick(appealSrc.problem_description, appealIn.problem_description),
    operation_mode: pick(appealSrc.operation_mode, appealIn.operation_mode, 'assist'),
    estimated_cost: pick(appealSrc.estimated_cost, appealIn.estimated_cost),
    estimated_time: pick(appealSrc.estimated_time, appealIn.estimated_time),
    parts_owner: pick(appealSrc.parts_owner, appealIn.parts_owner),
    is_new: pick(appealSrc.is_new, appealIn.is_new)
  };
}

// Пробрасываем touchpoint_direction
const touchpoint_direction = src.touchpoint_direction || 'mutual';

const out = {
  ...src,
  ...(channel != null ? { channel } : {}),
  ...(external_chat_id != null ? { external_chat_id } : {}),
  ...(external_user_id != null ? { external_user_id } : {}),
  ...(external_message_id != null ? { external_message_id } : {}),
  ...(timestamp != null ? { timestamp } : {}),
  ...(text ? { text } : {}),
  ...(Object.keys(client).length ? { client } : {}),
  ...(appeal ? { appeal, appeal_id: appeal.id } : {}),
  touchpoint_direction: touchpoint_direction
};

if (out.success === undefined) out.success = true;

return out;

