const baseData = $('Attach Base').first()?.json || {};
const sqlResult = $input.first()?.json;

let appealRow = null;
let appealFound = false;

if (sqlResult) {
  if (Array.isArray(sqlResult)) {
    if (sqlResult.length > 0) {
      appealRow = sqlResult[0];
      appealFound = true;
    }
  } else if (sqlResult.id) {
    appealRow = sqlResult;
    appealFound = true;
  }
}

const output = {
  ...baseData,
  appeal_found: appealFound
};

if (appealRow) {
  output.appeal = {
    id: appealRow.id,
    stage: appealRow.stage,
    phone_model: appealRow.phone_model,
    model_id: appealRow.model_id,
    brand_id: appealRow.brand_id,
    repair_type_id: appealRow.repair_type_id,
    problem_description: appealRow.problem_description,
    operation_mode: appealRow.operation_mode,
    estimated_cost: appealRow.estimated_cost,
    estimated_time: appealRow.estimated_time,
    parts_owner: appealRow.parts_owner,
    is_new: false
  };
}

return output;
