const input = $input.first()?.json || {};
console.log('═══ ELO DIALOG ENGINE START ═══');
console.log('Channel:', input.channel);
console.log('Tenant ID:', input.tenant_id);
console.log('External chat ID:', input.external_chat_id);
return input;

const allItems = $input.all();

console.log('═══ MERGE AFTER CLIENT ═══');

let baseData = null;
let clientResult = null;

for (const item of allItems) {
  const json = item.json;
  if (json?.tenant_id && json?.channel) {
    baseData = json;
  }
  if (json?.id && json?.client_found !== undefined) {
    clientResult = json;
  }
}

if (!baseData) {
  try {
    baseData = $('Attach Base').first()?.json || {};
  } catch (e) {
    baseData = {};
  }
}

const output = {
  ...baseData,
  client_found: clientResult?.client_found || false,
  client: clientResult ? {
    id: clientResult.id,
    name: clientResult.name,
    phone: clientResult.phone,
    telegram_id: clientResult.telegram_id
  } : null
};

console.log('Client found:', output.client_found);
console.log('Client ID:', output.client?.id);

return output;

const allItems = $input.all();

let baseData = null;
let dialogResult = null;

for (const item of allItems) {
  const json = item.json;
  if (json?.client?.id) {
    baseData = json;
  }
  if (json?.id && json?.status) {
    dialogResult = json;
  }
}

if (!baseData) {
  try {
    baseData = $('Merge After Client').first()?.json || {};
  } catch (e) {
    baseData = {};
  }
}

const dialogFound = dialogResult && dialogResult.id;

const output = {
  ...baseData,
  dialog_found: dialogFound || false,
  dialog: dialogFound ? {
    id: dialogResult.id,
    status: dialogResult.status,
    context: dialogResult.context || {},
    channel: dialogResult.channel,
    external_chat_id: dialogResult.external_chat_id,
    assigned_operator_id: dialogResult.assigned_operator_id,
    is_new: false
  } : null
};

console.log('Dialog found:', output.dialog_found);

return output;

const inputData = $input.first()?.json || {};
const existingDialog = inputData.dialog;

console.log('═══ USE EXISTING DIALOG ═══');
console.log('Dialog ID:', existingDialog?.id);

return {
  ...inputData,
  dialog: {
    ...existingDialog,
    is_new: false
  }
};

const sqlResult = $input.first()?.json;
const inMessage = $('Merge After Dialog').first()?.json || {};

if (!sqlResult || !sqlResult.id) {
  throw new Error('No dialog data returned from SQL');
}

console.log('═══ NEW DIALOG CREATED ═══');
console.log('Dialog ID:', sqlResult.id);

return {
  ...inMessage,
  dialog: {
    id: sqlResult.id,
    status: sqlResult.status,
    context: sqlResult.context || {},
    channel: sqlResult.channel,
    external_chat_id: sqlResult.external_chat_id,
    assigned_operator_id: sqlResult.assigned_operator_id,
    is_new: true
  }
};

const sqlResult = $input.first()?.json || {};
let srcData = null;

try {
  srcData = $('Use Existing Dialog').first()?.json || $('Merge New Dialog').first()?.json;
} catch (e) {
  srcData = {};
}

if (!srcData) {
  srcData = {};
}

const output = {
  ...srcData,
  event_id: sqlResult.id,
  success: true
};

console.log('═══ MESSAGE SAVED AS EVENT ═══');
console.log('Event ID:', output.event_id);
console.log('Dialog ID:', output.dialog?.id);

return output;

// Prepare final output for AI Router
let srcData = null;
try {
  srcData = $('Prepare for AI Router').first()?.json;
} catch (e) {
  srcData = $input.first()?.json || {};
}

return {
  tenant_id: srcData.tenant_id,
  dialog: srcData.dialog,
  client: srcData.client,
  text: srcData.text,
  channel: srcData.channel,
  external_chat_id: srcData.external_chat_id,
  external_user_id: srcData.external_user_id,
  media: srcData.media,
  meta: srcData.meta,
  event_id: srcData.event_id,
  client_found: srcData.client_found,
  success: true
};
